mapper.select:
SELECT co.contractdetails_id,btp.tender_projects_id,btp.tender_projects_name,co.contractdetails_name,co.contractdetails_number,ve.abbreviated,ve.vendor_id,ag.applicant,co.open_time,co.contractdetails_amount,ag.applicant_time,co.contractdetails_type,co.contractdetails_state,co.contractdetails_payment_id FROM public_agreement AS ag INNER JOIN public_contractdetails AS co ON(ag.contract_id=co.contract_id) INNER JOIN public_vendor AS ve ON(co.vendor_contract_id=ve.vendor_id) INNER JOIN bidding_tender_projects AS btp ON(ag.contract_projects_id=btp.tender_projects_id)
co.contractdetails_number like concat('%', #{contractdetailsNumber}, '%') and co.contractdetails_name like concat('%', #{contractdetailsName}, '%') and co.contractdetails_state = #{contractdetailsState} and ve.abbreviated like concat('%', #{abbreviated}, '%') and ve.vendor_id=#{vendorId}
ORDER BY co.open_time DESC

mapper.update:
update public_contractdetails
contract_id = #{contractId}, contractdetails_number = #{contractdetailsNumber}, contractdetails_payment_id = #{contractdetailsPaymentId}, contractdetails_name = #{contractdetailsName}, contractdetails_type = #{contractdetailsType}, open_time = #{openTime}, contractdetails_amount = #{contractdetailsAmount}, contractdetails_level = #{contractdetailsLevel}, illustrate = #{illustrate}, bid_end = #{bidEnd}, vendor_contract_id = #{vendorContractId}, contractdetails_state = #{contractdetailsState}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where contractdetails_id = #{contractdetailsId}
